    // Event handler for btnAdditionCalculate. Carries out an addition operation under modulus.
    private void btnAdditionCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAdditionCalculateActionPerformed
        // get input from user interface
        String primaryString = txtbAdditionPrimary.getText();
        String secondaryString = txtbAdditionSecondary.getText();
        String modulusString = txtbAdditionTertiary.getText();
        // check that all fields have been filled out
        if (primaryString.length() == 0 || secondaryString.length() == 0 || modulusString.length() == 0) {
            JOptionPane.showMessageDialog(this, "Not all fields filled out.", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        try {
            // parse values
            int primary = Integer.parseInt(primaryString);
            int secondary = Integer.parseInt(secondaryString);
            int modulus = Integer.parseInt(modulusString);
            // carry out addition and display result
            int result = ModCalculator.Addition(primary, secondary, modulus);
            lblAdditionResult.setText(Integer.toString(result));
        }
        // invalid value entered, show error dialog
        catch (NumberFormatException ex) {
            JOptionPane.showMessageDialog(this, "Invalid field.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnAdditionCalculateActionPerformed

