    // Event handler for btnInverseCalculate. Carries out an inverse operation under modulus.
    private void btnInverseCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInverseCalculateActionPerformed
        // get input from user interface
        String primaryString = txtbInversePrimary.getText();
        String modulusString = txtbInverseSecondary.getText();
        // check that all fields have been filled out
        if (primaryString.length() == 0 || modulusString.length() == 0) {
            JOptionPane.showMessageDialog(this, "Not all fields filled out.", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        try {
            // parse values
            int primary = Integer.parseInt(primaryString);
            int modulus = Integer.parseInt(modulusString);
            // carry out inverse calculation and display result
            int result = ModCalculator.GetModularInverse(primary, modulus);
            lblInverseResult.setText(Integer.toString(result));
        }
        // invalid value entered, show error dialog
        catch (NumberFormatException ex) {
            JOptionPane.showMessageDialog(this, "Invalid field.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnInverseCalculateActionPerformed

    // Event handler for btnSquareRootCalculate. Carries out a square root operation under modulus.
    private void btnSquareRootCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSquareRootCalculateActionPerformed
        // get input from user interface
        String primaryString = txtbSquareRootPrimary.getText();
        String modulusString = txtbSquareRootSecondary.getText();
        // check that all fields have been filled out
        if (primaryString.length() == 0 || modulusString.length() == 0) {
            JOptionPane.showMessageDialog(this, "Not all fields filled out.", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        try {
            // parse values
            int primary = Integer.parseInt(primaryString);
            int modulus = Integer.parseInt(modulusString);
            // carry out square root calculation and display result
            int result = ModCalculator.GetSquareRoot(primary, modulus);
            // display result or no sq root
            if (result != -1) {
                lblSquareRootResult.setText(Integer.toString(result));
            }
            else {
                lblSquareRootResult.setText("no sq root");
            }
        }
        // invalid value entered, show error dialog
        catch (NumberFormatException ex) {
            JOptionPane.showMessageDialog(this, "Invalid field.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnSquareRootCalculateActionPerformed

